let explode s =
    let rec exp i l =
        if i < 0 then l else exp (i - 1) (s.[i] :: l) in
    exp (String.length s - 1) []

let solve_part1 =
    let chars = explode data in
    let ups =
        chars
        |> List.filter (fun c -> c = '(')
        |> List.length
    in
    ups - (List.length chars - ups)

let solve_part2 =
    let chars = explode data in
    let rec loop step curr_floor idx =
        match step with
        | '(' :: rest -> loop rest (curr_floor + 1) (idx + 1)
        | ')' :: rest -> if curr_floor = 0 then idx else loop rest (curr_floor - 1) (idx + 1)
        | _ -> -1
    in
    loop chars 0 1
